import React, { useState } from "react";
import { styles } from "./homeScreen.style";
import { useMutation, gql } from "@apollo/client";
import { Button, View, TextInput } from "react-native";
import { idToUpdate } from "./getTodo";

const UPDATE_TODO = gql`
  mutation UpdateTodo($updateTodoId: Int!, $updateTodoDescription2: String!) {
    updateTodo(id: $updateTodoId, description: $updateTodoDescription2) {
      id
      description
      isFinished
    }
  }
`;

const UpdateTodo = () => {
  const [input, setInput] = useState<string>("");

  const [updateTodo, { loading, error }] = useMutation(UPDATE_TODO, {
    refetchQueries: [
      {
        query: gql`
          query Todos {
            todos {
              id
              description
              isFinished
            }
          }
        `,
      },
    ],
  });
  if (loading) return "Updating...";
  if (error) return `Update error! ${error.message}`;

  const onInputChange = (value: any) => {
    setInput(value);
  };

  const updateT = () => {
    // updateTodo({
    //   variables: {
    //     id: idToUpdate,
    //     description: input,
    //   },
    // });
  };

  return (
    <View>
      <TextInput
        placeholder="Enter new desctiption here.."
        value={input}
        onChangeText={onInputChange}
        style={styles.inputTodo}
      />
      <Button title="Update todo" onPress={updateT} />
    </View>
  );
};

export default UpdateTodo;
