import React, { useContext } from "react";
import { View, Text, Button } from "react-native";
import AuthContext from "../../../context/authContext/AuthContext";
import { styles } from "./homeScreen.style";
import { useQuery, gql, useMutation } from "@apollo/client";

import BouncyCheckbox from "react-native-bouncy-checkbox";



const ADD_TODO = gql`
  mutation AddTodo($description: String!) {
    addTodo(description: $description) {
      id
      description
      isFinished
    }
  }
`;

const HomeScreen = () => {
  const { userSignout } = useContext(AuthContext);


  return (
    <View style={styles.container}>
      <View style={styles.Thead}>
        <Text style={styles.headline}>TODO</Text>
      </View>

      <View style={styles.todo_fetch}>
        {data.todos.map((todo: { id: React.Key | null | undefined; description: boolean | React.ReactChild | React.ReactFragment | React.ReactPortal | null | undefined; }) => (
          <View style={styles.todo_container} key={todo.id}>
            <View style={styles.todo_item}>
              <Text>{todo.description}</Text>
            </View>
            <BouncyCheckbox
              size={25}
              fillColor="blue"
              unfillColor="#FFFFFF"
              iconStyle={{ borderColor: "blue" }}
              textStyle={{ fontFamily: "JosefinSans-Regular" }}
              onPress={(isChecked: boolean) => {}}
            />
          </View>
        ))}
      </View>

      <View style={styles.Tbottom}>
        <Button title="add todo" onPress={userSignout} />
        <Button testID="btn-signout" title="LOG OUT" onPress={userSignout} />
      </View>
    </View>
  );
};
export default HomeScreen;
